import React, { useEffect, useState } from 'react'
import { NavLink, useNavigate } from 'react-router-dom'


const Home = () => {

  const [list, setList] = useState([])

  const getData = async () => {

    const res = await fetch("/api/getproducts", {
      method: "GET",
      headers: {
        "Content-Type": "application/json"
      }
    });

    const productData = await res.json();
    if (res.status === 422 || !productData) {
      console.log("error");
    } else {
      
      setList(productData)

    }
  }
  
  useEffect(() => {
    getData()
    
  }, [])
  const deleteProductHandler = async (id) => {
    const res2 = await fetch(`/deleteproduct/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json"
      }
    })

    const deletedProductData = await res2.json();
    console.log(deletedProductData);

    if (res2.status === 422 || !deletedProductData) {
      console.log("error");
    }
    else {
      console.log("product deleted");
      getData()
    }
  }


  return (
    <div>
      {
        list.length>0?

        list.map((item) => {
          return (
            <div>
              <img variant="top" style={{ width: "100px", textAlign: "center", margin: "auto" , height:"100px" }} src={`/uploads/${item.imgpath}`} className='mt-2' />
              name :{item.product_name} || 
              selling_price :{item.selling_price} ||
              actual_price :{item.actual_price} ||
              discount : {item.discount} ||
              description :{item.description} ||
              product_category : {item.product_category?.catName} ||
              product_brand : {item.product_brand?.bname} ||
              countInStock :{item.countInStock}
               <NavLink to={`/editproduct/${item._id}`}>  <button className="btn btn-primary">Edit</button></NavLink>
              <button onClick={() => deleteProductHandler(item._id)}>Delete</button>
            </div>
            )
          })
          :
          ""
      }
      <button onClick={addProductHandler}>Add</button>
    </div>
  )
}

export default Home
