import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import {AiFillEye} from 'react-icons/ai';
import "../../CSS/mix.css"

const Register = () => {
  const [showPass,setShowPass] = useState(false)
  const [cshowpass,setCShowPass] = useState(false)

  const [fname,setFname] = useState("")
  const [email,setEmail] = useState("")
  const [password,setPassword] = useState("")
  const [cpassword,setCpassword] = useState("")


  const addUserdata  = async (e) =>{
    e.preventDefault();
    

    if(fname===''){
      alert("Enter Name")
    }else if (email ===''){
      alert("Enter Email")
    }else if(!email.includes("@")){
      alert("Enter Valid Email")
    }else if(password === ""){
      alert("Enter your password")
    }else if(password.length<6){
      alert("Please Enter Min 6 character")
    }else if(cpassword === ''){
      alert("Please enter Confirm Password")
    }else if(cpassword.length<6){
      alert("Please Enter Min 6 Character")
    }else if(password!==cpassword){
        alert("password and confirm password not match")
    }else{
      
     
      const data = await fetch("http://localhost:8009/register", {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            fname, email, password, cpassword
        })
    });

    const res = await data.json();
    console.log(res.status);

    if(res.status === 201){
      alert("user registration done");
     
      setFname("")
      setEmail("")
      setPassword("")
      setCpassword("")
    }
  


    }
  }
  return (
    <>
    <section>
      <div className='form_data'>
        <div className='form_heading'>
          <h1>Sign Up</h1>
          <p style={{textAlign:"center"}}>We are glad that you will be using Project Cloud to manage<br/>
          your tasks! We hope that you will get like it</p>
        </div>
        <form>
        <div className='form_input'>
            <label htmlFor='name'>Name</label>
            <input type="fname"
             onChange={(e) => setFname(e.target.value)}
            value={fname} 
            name="fname" 
            placeholder='Enter Your Name' />
          </div>
          <div className='form_input'>
            <label htmlFor='email'>Email</label>
            <input type="email" 
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            name="email" 
            placeholder='Enter Your Email Address' />
          </div>
          <div className='form_input'>
            <label htmlFor='p'>Password</label>
            <div className='two'>
              <input 
              type={!showPass ? "password" : "text"}
              onChange={(e) => setPassword(e.target.value)}
              value={password}
              name="password"
              placeholder='Enter Your Password' />

              <div className='showpass' onClick={() =>setShowPass(!showPass)}>
               {
                !showPass ? "Show" : "Hide"
               }              
              </div>        
            </div>
          </div>
          <div className='form_input'>
            <label htmlFor='p'>Confirm Password</label>
            <div className='two'>
              <input type={!cshowpass ? "password" : "text"} onChange={(e) => setCpassword(e.target.value)} value={cpassword} name="cpassword" placeholder='Confirm Password' />
              <div className='showpass' onClick={() =>setCShowPass(!cshowpass)}>
               {
                !cshowpass ? "Show" : "Hide"
               }              
                </div>        
            </div>
          </div>
          <button className='btn' onClick={addUserdata}>SignUp</button>
          <p>Already have an Account?  <NavLink to={"/"}>Log In</NavLink></p>
        </form>
      </div>
    </section>
    </>
  )
}

export default Register
